import React, { useState } from 'react';

const AdminPanel: React.FC = () => {
  // Use state to keep track of form values
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('new'); // Assuming 'new' as the default status

  // Handle form submission
  const handleSubmit = () => {
    // Logic to save or process the data (e.g., save to a database or call an API)
    console.log('Form submitted:', { name, email, description, status });
  };

  return (
    <div className="container mt-4">
      <h2>Admin Panel</h2>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col">Description</th>
            <th scope="col">Status</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>
              <input 
                type="text" 
                placeholder="Name" 
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </td>
            <td>
              <input 
                type="email" 
                placeholder="Email" 
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </td>
            <td>
              <input 
                type="text" 
                placeholder="Description" 
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </td>
            <td>
              <select 
                value={status}
                onChange={(e) => setStatus(e.target.value)}
              >
                <option value="new">New</option>
                <option value="in progress">In Progress</option>
                <option value="resolved">Resolved</option>
              </select>
            </td>
          </tr>
        </tbody>
      </table>
      <button className="btn btn-primary" onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default AdminPanel;
